package aaa;

public class Aaa {

	public static void main(String[] args) {
		int k;
		// TODO Auto-generated method stub
for(int i=7; i>0; i--){
	for(k=0; k<i; k++){
		System.out.printf(" ");
		}
		for(k=7; k>=i; k--){
		System.out.printf("*");
		}
	System.out.printf("\n");
	}
for(int i=0; i<6; i++){
	for(k=0; k<=i+1; k++){
		System.out.printf(" ");
		}
		for(k=5; k>=i; k--){
		System.out.printf("*");
		}
	System.out.printf("\n");
	}
	}
}







