package aaa;

public class Aaa {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
for(int i=1; i<=5; i++) {
	for(int k=1; k<=i; k++) {
		System.out.printf("*");
		
	}
	System.out.printf("\n");
}
	for(int i=6; i>=1; i--) {
		for(int k=1; k<=i; k++) {
			System.out.printf("*");
		}
		System.out.printf("\n");
	}
}

	
}
