package aaa;

public class Aaa {

	public static void main(String[] args) {
		int k;
		// TODO Auto-generated method stub
for(int i=6; i>0; i--){
	for(k=0; k<i; k++){
		System.out.printf(" ");
		}
		for(k=6; k>=i; k--){
		System.out.printf("*");
		}
	System.out.printf("\n");
	}
	}
}







